import React, { Component } from "react";
import Header from "./header";
import BirdPanel from "./birdPanel";
import WildPanel from "./wildPanel";
import DomesticPanel from "./domesticPanel";

class Main extends Component {
  state = {
    activePanel: "bird",
  };

  renderPanel() {
    switch (this.state.activePanel) {
      case "domestic":
        return <DomesticPanel />;
      case "wild":
        return <WildPanel />;
      case "bird":
        return <BirdPanel />;
    }
  }

  handleChangePanel(newPanel){
    let np = '';
    switch (newPanel){
      case "domestic":
        return <DomesticPanel />;
      case "wild":
        return <WildPanel />;
      case "bird":
        return <BirdPanel />;
    }

  }

  render() {
    return (
      <div>
        <Header 
          activePanel={this.state.activePanel}
          onChangePanel={this.handleChangePanel}
        />
        <div>{this.renderPanel()}</div>
      </div>
    );
  }
}

export default Main;
