import React, { Component } from "react";
import Header from "./header";

class Main extends Component {
  state = {
    activePanel: "domestic",
  };

  renderPanel(){
    switch (this.state.activePanel) {
      case ("domestic"):
        return <DomesticPanel/>
      case ("wild"):
        return <WildPanel/>
      case ("bird"):
        return <BirdPanel/>
    }
  }

  render() {
    return (
      <div>
        <Header activePanel={this.state.activePanel} />
        <div>
          {this.renderPanel()}
        </div>
      </div>
    )
  }
}

export default Main;
